<?php
 namespace App\Http\Controllers; use App\Models\accounts; use App\Models\expenses; use App\Models\products; use App\Models\purchase_details; use App\Models\sale_details; use App\Models\sales; use Carbon\Carbon; use Illuminate\Container\Attributes\DB; use Illuminate\Http\Request; class dashboardController extends Controller { public function index() { $months = array(); for ($i = 0; $i < 12; $i++) { $date = Carbon::now()->subMonths($i); $firstDay = $date->copy()->firstOfMonth()->toDateString(); $lastDay = $date->copy()->lastOfMonth()->toDateString(); $monthName = $date->format("\x4d"); $months[] = array("\146\x69\x72\163\164" => $firstDay, "\154\141\x73\164" => $lastDay, "\156\141\x6d\x65" => $monthName); } $months = array_reverse($months); $sales = array(); $monthNames = array(); $expenses = array(); $products = products::all(); $profits = array(); $last_sale = 0; $last_expense = 0; $last_profit = 0; foreach ($months as $key => $month) { $first = $month["\146\151\x72\x73\x74"]; $last = $month["\x6c\x61\163\x74"]; $sale = sales::whereBetween("\144\141\x74\145", array($first, $last))->count(); $expense = expenses::whereBetween("\144\x61\164\x65", array($first, $last))->sum("\141\x6d\157\x75\x6e\164"); $sales[] = $sale; $expenses[] = $expense; $monthNames[] = $month["\156\x61\155\x65"]; $profit = 0; foreach ($products as $product) { $purchases = purchase_details::where("\160\x72\x6f\x64\165\x63\164\111\104", $product->id)->whereBetween("\144\141\x74\145", array($first, $last)); $purchases_amount = $purchases->sum("\141\x6d\x6f\x75\x6e\164"); $purchases_qty = $purchases->sum("\x71\164\171"); $purchase_qty = $purchases_qty; if ($purchase_qty > 0) { $purchase_price = $purchases_amount / $purchase_qty; } else { $purchase_price = 0; } $sales1 = sale_details::where("\x70\162\x6f\x64\x75\x63\x74\111\x44", $product->id)->whereBetween("\144\x61\164\x65", array($first, $last)); $sales_amount = $sales1->sum("\x61\155\157\165\156\164"); $sales_qty = $sales1->sum("\161\164\171"); if ($sales_qty > 0) { $sale_price = $sales_amount / $sales_qty; } else { $sale_price = 0; } $ppi = $sale_price - $purchase_price; $ppp = $ppi * $sales_qty; $profit += $ppp; } $profits[] = $profit - $expense; $last_sale = $sale; $last_expense = $expense; $last_profit = $profit; } $topProducts = products::withSum("\163\x61\x6c\x65\x44\x65\164\141\151\154\x73", "\x71\164\171")->withSum("\163\x61\x6c\x65\x44\145\x74\141\151\154\163", "\x61\x6d\157\165\156\164")->orderByDesc("\163\141\x6c\x65\x5f\x64\x65\x74\141\x69\154\x73\x5f\163\x75\155\137\161\164\171")->take(5)->get(); $topProductsArray = array(); foreach ($topProducts as $product) { $stock = getStock($product->id); $price = avgSalePrice("\141\x6c\x6c", "\x61\154\x6c", $product->id); $topProductsArray[] = array("\x6e\x61\155\x65" => $product->name, "\x70\x72\x69\x63\x65" => $price, "\x73\x74\x6f\x63\x6b" => $stock, "\x61\x6d\157\x75\x6e\x74" => $product->sale_details_sum_amount, "\x73\x6f\154\144" => $product->sale_details_sum_qty); } $topCustomers = accounts::where("\164\x79\x70\x65", "\x43\x75\x73\x74\x6f\155\x65\x72")->withSum("\163\x61\x6c\x65", "\164\x6f\164\x61\x6c")->orderByDesc("\163\141\154\x65\137\163\x75\155\137\164\157\x74\x61\x6c")->take(5)->get(); $topCustomersArray = array(); foreach ($topCustomers as $customer) { if ($customer->id != 2) { $balance = getAccountBalance($customer->id); $customer_purchases = $customer->sale_sum_total; $topCustomersArray[] = array("\x6e\x61\155\145" => $customer->title, "\x70\165\162\x63\150\x61\163\x65\163" => $customer_purchases, "\x62\141\154\141\156\143\x65" => $balance); } } return view("\144\141\x73\x68\142\157\x61\x72\144\x2e\x69\x6e\144\x65\x78", compact("\x73\141\154\x65\x73", "\155\x6f\x6e\x74\x68\116\141\x6d\x65\x73", "\x65\x78\x70\145\x6e\163\145\x73", "\160\x72\x6f\x66\151\164\x73", "\x6c\141\x73\164\137\163\x61\154\x65", "\x6c\x61\163\x74\137\x65\x78\x70\145\x6e\163\x65", "\x6c\x61\163\164\137\x70\x72\157\x66\151\164", "\164\157\160\120\162\157\144\x75\x63\16